import React, { Component, useState } from "react";
import reactDom from "react-dom";
//import {Button} from 'bootstrap/dist/css/bootstrap.css';
import "./background.css";
import Modal from 'bootstrap/dist/css/bootstrap.css';


export default function CustomButton(props) {
  const { id, img, active, setActive } = props;
  const [alreadyClicked, setAlreadyClicked] = useState(false);

  function Example() {
    const [show, setShow] = useState(false);
  
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
  
    return (
      <>
        <Button variant="primary" onClick={handleShow}>
          Launch demo modal
        </Button>
  
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
  
  render(<Example />);

  function handleClick() {
    //setAlreadyClicked(true);
    setActive(id);
  }

  function cancelClick() {
    setAlreadyClicked(true);
    setActive(id);
  }

  return (
    <div>
      <button
        onClick={handleClick}
        onMouseLeave={cancelClick}
        id={id}
        className={
          active ? "active" : alreadyClicked ? "alreadyClicked" : "notClicked"
        }
      >
        <img src={img} alt={id} />

      </button>
    </div>
  );
}
