import React, { Component , useState } from 'react';
import reactDom from 'react-dom';
//import {Button} from 'bootstrap/dist/css/bootstrap.css';
import './background.css';


export default function CustomButton(props){
    const{id,img,active,setActive} = props;
    const[alreadyClicked, setAlreadyClicked] = useState(false);
    const[checked, setChecked] = useState(false);

    function handleClick(){
        setAlreadyClicked(true);
        setActive(id);

    }

    function cancelClick(){
        setAlreadyClicked(false);
        setActive(id);
        setChecked(true);

    }

    function isChecked(){
        setChecked(true);

    }


    return(
        <button onMouseEnter ={handleClick} onMouseLeave={cancelClick} id = {id} 
        className={ alreadyClicked ? "alreadyClicked" : "notClicked"} >
            <div className = {checked ? "alreadyChecked" : "notChecked"}></div>
            <img src = {img} alt={id} />

        </button>
    )
}
