import React, { Component, useState } from "react";
import reactDom from "react-dom";
import "./background.css";
import CustomModal from "./customModal";
import Checkmark from "./assets/icon-check-mark.svg";
import content01 from "./assets/image-content-01.jpg";
import content02 from "./assets/image-content-02.jpg";
import content03 from "./assets/image-content-03.jpg";
import content04 from "./assets/image-content-04.jpg";
import content05 from "./assets/image-content-05.jpg";
import content06 from "./assets/image-content-06.jpg";
import content07 from "./assets/image-content-07.jpg";
import content08 from "./assets/image-content-08.jpg";
import content09 from "./assets/image-content-09.jpg";

const modals = [
  {id: "001" , img:content01},
  {id: "002" , img:content02},
  {id: "003" , img:content03},
  {id: "004" , img:content04},
  {id: "005" , img:content05},
  {id: "006" , img:content06},
  {id: "007" , img:content07},
  {id: "008" , img:content08},
  {id: "009" , img:content09},
];

export default function CustomButton(props) {
  const { id, img, active, setActive, mod } = props;
  const [alreadyClicked, setAlreadyClicked] = useState(false);

  const [modalShow, setModalShow] = useState(false);

  function handleClick() {
    setActive(id);
    setModalShow(true);
  }

  function cancelClick() {
    setAlreadyClicked(true);
    setActive(id);
    setModalShow(false);
  }

  return (
    <div class="customButton">
      <button
        onClick={handleClick}
        onMouseLeave={cancelClick}
        id={id}
        img={img}
        className={
          active ? "active" : alreadyClicked ? "alreadyClicked" : "notClicked"
        }
        mod={mod}
      >
        <img src={img} />

        <div
          className="checkButton"
          style={{ display: alreadyClicked ? "block" : "none" }}
        >
          <img src={Checkmark} />
        </div>
      </button>

      <div className="modal-div"></div>
      <CustomModal
        id={mod}
        img={modals[mod - 1].img}
        show={modalShow}
        onHide={() => setModalShow(false)}
      />
    </div>
  );
}
