import React, { Component, useState } from "react";
import reactDom from "react-dom";
//import {Button} from 'bootstrap/dist/css/bootstrap.css';
import "./background.css";
import cont from "./assets/bg-content.png";
import CustomModal from "./customModal";
import Modal from "react-modal";
import IconDotLine from './assets/icon-dot-line.png';
import { nodeName } from "jquery";

Modal.setAppElement("#root");

const modals = [
  {img : IconDotLine , text : "hello"}
];

export default function CustomButton(props) {
  const { id, img, active, setActive, content } = props;
  const [alreadyClicked, setAlreadyClicked] = useState(false);
  const[isOpen, setIsOpen] = useState(false);


  function toggleModal(){
    setIsOpen(!isOpen);
}


  function handleClick() {
    //setAlreadyClicked(true);
    setActive(id);
    toggleModal();
  }

  function cancelClick() {
    setAlreadyClicked(true);
    setActive(id);
    toggleModal();
  }

  return (
    <button
      onClick={(handleClick)}
      onMouseLeave={(cancelClick)}
      id={id}
      className={
        active ? "active" : alreadyClicked ? "alreadyClicked" : "notClicked"
      }
    >
      <img src={img} alt={id} />

      <div className="modal-div" style = {{backgroundImage : cont} }>
        {modals.map((modal) => (
          <Modal
          isOpen={isOpen}
          onRequestClose={toggleModal} 
          img = {modal.img}
          >
          
             
            
            {modal.text}
          </Modal>
            
        ))}
        
      </div>
    </button>
  );
}
