import React, { Component, useState } from "react";
import reactDom from "react-dom";
import BGimg from "./assets/bg.jpg";
import IconDotLine from "./assets/icon-dot-line.png";
import "./background.css";
import CustomButton from "./customButton";
import IconOne from "./assets/icon-01.png";
import IconTwo from "./assets/icon-02.png";
import IconThree from "./assets/icon-03.png";
import IconFour from "./assets/icon-04.png";
import IconFive from "./assets/icon-05.png";
import IconSix from "./assets/icon-06.png";
import IconSeven from "./assets/icon-07.png";
import IconEight from "./assets/icon-08.png";
import IconNine from "./assets/icon-09.png";
import CustomModal from "./customModal";
import Checkmark from "./assets/icon-check-mark.png";
import content01 from "./assets/image-content-01.jpg";
import content02 from "./assets/image-content-02.jpg";
import content03 from "./assets/image-content-03.jpg";

const buttons = [
  { id: "num1", img: IconOne },
  { id: "num2", img: IconTwo },
  { id: "num3", img: IconThree },
  { id: "num4", img: IconFour },
  { id: "num5", img: IconFive },
  { id: "num6", img: IconSix },
  { id: "num7", img: IconSeven },
  { id: "num8", img: IconEight },
  { id: "num9", img: IconNine },
];

const modals = [
  {  num1 : {img: content01} },
  {  num2 :  {img: content02} },
  {  num3 :  {img: content03} },
]
;

export default function Background() {
  const [active, setActive] = useState(null);


  return (
    <div className="Background">
      <img src={BGimg} />

      <div>
        <img src={IconDotLine} id="IconSetup" />

        <div className="button-div">
          {buttons.map((button) => (
            <CustomButton
              id={button.id}
              img={button.img}
              active={active === button.id ? true : false}
              setActive={(id) => setActive(id)}
            />
          ))}
        </div>
        <div className="modal-div" >

<CustomModal 
  id = {active}
  img = {modals[active]} 
  show = {modalShow}
  onHide= {() => setModalShow(false)} 
/>

</div>
      </div>
    </div>
  );
}
