import reactDom from 'react-dom';
import {Button} from 'bootstrap/dist/css/bootstrap.css';
import './background.css';


export default function CustomButton(props){
    const{id,img,active,setActive} = props;
    const[alreadyClicked, setAlreadyClicked] = useState(false);

    var well= {
        boxShadow: "1px 2px 1px #0ff"
    }

    var notWell = {
        boxShadow: "1px 2px 1px #fff"
    }

    function handleClick(){
        setAlreadyClicked(true);
        setActive(id);

    }


    return(
        <button onClick= {handleClick} className={active ? "active" : alreadyClicked ? "alreadyClicked" : "notClicked"}>
            <img src = {img} className = {id} style = {className} />
      
        </button>
    )
}
