import React, { Component, useState } from "react";
import reactDom from "react-dom";
//import {Button} from 'bootstrap/dist/css/bootstrap.css';
import "./background.css";
import cont from "./assets/bg-content.png";
import CustomModal from "./customModal";
import Modal from "react-modal";
import IconDotLine from "./assets/icon-dot-line.png";

Modal.setAppElement('#root');

const modals = [{ img: IconDotLine }];

export default function CustomButton(props) {
  const { id, img, active, setActive } = props;
  const [alreadyClicked, setAlreadyClicked] = useState(false);

  const [isOpen, setIsOpen] = useState(false);
  
  function modalContent(props){
    const {id, img} = props;
    

    {modals.map((modal) => (
      <Modal isOpen = {isOpen} onRequestClose={toggleModal}>
      <img src = {modal.img} alt={modal.id} />
    </Modal>
    ))}

  }

  function toggleModal(){
    setIsOpen(!isOpen);
  } 

  function handleClick() {
    //setAlreadyClicked(true);
    setActive(id);
  }

  function cancelClick() {
    setAlreadyClicked(true);
    setActive(id);
  }

  return (
    <div>
      <button
        onClick={handleClick, toggleModal}
        onMouseLeave={cancelClick, toggleModal}
        id={id}
        className={
          active ? "active" : alreadyClicked ? "alreadyClicked" : "notClicked"
        }
      >
        <img src={img} alt={id} />

      </button>
    </div>
  );
}
