import React, { useState } from "react";
import reactDom from "react-dom";
import Modal from "react-bootstrap/Modal";
import prevButton from "./assets/button-prev.png";
import nextButton from "./assets/button-next.png";
import Button from "react-bootstrap/Button";


export default function CustomModal(props) {
  const { id, img, prev, next, current, label } = props;
  const [modalShow, setModalShow] = useState(null);


  return (
    <Modal
      show={modalShow}
      onHide={() => setModalShow(false)}
      {...props}
      size="md"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header>
        <Modal.Title id="contained-modal-title-vcenter"></Modal.Title>
      </Modal.Header>

      <Modal.Body >
        <h4></h4>
<div>
       
{(() => {
  
  switch (label) {
     case 'current':
         return (
           id
         )
     case 'prev':
         return (
           id = id - 1
         )
      case 'next':
          return(
            id = id - 1
          )
     default:
         return (
           id
         )
  }

})()}
</div>
        
      </Modal.Body>
      <Modal.Footer>
      <Button className="prev" onClick={()=> label = 'prev'} >
          <img src = {prevButton} />
        </Button>
        <Button className="next" onClick={()=> label ='next'} >
          <img src = {nextButton} />
        </Button>

      </Modal.Footer>
    </Modal>
  );
}
