import React, { Component, useState } from "react";
import reactDom from "react-dom";
import "./background.css";
import CustomModal from "./customModal";
import Button from "react-bootstrap/Button";
import Checkmark from "./assets/icon-check-mark.svg";


export default function CustomButton(props) {
  const { id, img, active, setActive, seen, setSeen} = props;
  const [alreadyClicked, setAlreadyClicked] = useState(false);
  const [alreadySeen, setAlreadySeen] = useState(false);



  function handleClick() {
    setActive(id);
    setSeen(id);
    setAlreadyClicked(true);
  }

  function cancelClick() {
    //setAlreadyClicked(true);
    setActive(id);
    setSeen(id);
  }



 

  return (
    <div class="customButton">
      <div>
      <button
        onClick={handleClick}
        onMouseLeave={cancelClick}
        id={id}
        img={img}
        className={
          active ? "active" : alreadyClicked ? "alreadyClicked" : seen ? "alreadySeen" : "notClicked"
        }
        
      >
        <img src={img} />

        <div className = "checkButton"
          style={{ display: alreadyClicked ||  seen || active ? "block" : "none" }}
        >
          
          <img src={Checkmark} alt="checked" />
        </div>
      </button>
      </div>
   

    </div>
  );
}
