import React, { useState } from 'react';
import reactDom from 'react-dom';
import {Button} from 'bootstrap/dist/css/bootstrap.css';
import IconOne from './assets/icon-04.png'

export default function ButtonSetup(props){
    const {id, img, active, setActive} = props;
    const [alreadyClicked, setAlreadyClicked] = useState(false);

    function handleClick(){
        setAlreadyClicked = true;
        setActive = true;
    }

    return(
        <button className={active ? "active" : alreadyClicked ? "alreadyClicked" : "notClicked"}>
            <img src={"url("+ IconOne +")"} />

        </button>
    )
}
