import reactDom from "react-dom";
import "./background.css";
import CustomModal from "./customModal";
import Checkmark from "./assets/icon-check-mark.png";
import content01 from "./assets/image-content-01.jpg";
import content02 from "./assets/image-content-02.jpg";
import content03 from "./assets/image-content-03.jpg";



export default function CustomButton(props) {
  const { id, img, active, setActive} = props;
  const [alreadyClicked, setAlreadyClicked] = useState(false);




  function handleClick() {
    setActive(id);
    //setModalShow(true);
  }

  function cancelClick() {
    setAlreadyClicked(true);
    setActive(id);
    //setModalShow(false);
  }

  return (
    <div class = "customButton">
      
      <button
        onClick={handleClick}
        onMouseLeave={cancelClick}
        id={id}
        img={img}
        className={
          active ? "active" : alreadyClicked ? "alreadyClicked" : "notClicked"
        }
       
        
      >
        <img src={img} />
        
        <div
          className="checkButton"
          style={{ display: alreadyClicked ? "block" : "none" }}
        >
          <img src={Checkmark} />
          

        </div>
      </button>
