import React, { useState } from "react";
import reactDom from "react-dom";
import "./background.css";
import Checkmark from "./assets/icon-check-mark.svg";
import Fade from "react-reveal/Fade";
import Bounce from "react-reveal/Bounce";
import IconGlow from "./assets/icon-bg-start-here.svg";
import glow from "./assets/icon-bg-start-here.webm";
import { useEffect } from "react";

export default function CustomButton(props) {
  const { id, img, active, setActive, seen, mod, isModalOpen } = props;
  const [alreadyClicked, setAlreadyClicked] = useState(false);
  





  function handleClick() {
    setActive(id);
    setAlreadyClicked(true);
  }

  function cancelClick() {
    //setAlreadyClicked(true);
    setActive(id);
  }

  <div className = "iconGlow" 
style={{ display : id==="num1" && !alreadyClicked && !active 
&& seen.includes(id) 
? "block" : "none" }} >
      <video
      src={glow}
      autoPlay="true"
      loop="true"
    />
</div>

  return (
    

    <div class="customButton">


      <div>
        <Fade duration={1000} delay={mod * 300}>
          <button
            onClick={handleClick}
            onMouseLeave={cancelClick}
            id={id}
            img={img}
            className={
              active && id !== null
                ? "active"
                : alreadyClicked
                ? "alreadyClicked"
                : seen.includes(id)
                ? "seen"
                : !active && id !== null
                ? "notClicked"
                : "glow"
            }
          >
            <img src={img} />

    

            <div
              className="checkButton"
              style={{
                display:
                  seen.includes(id) && !active && !isModalOpen
                    ? "block"
                    : "none",
              }}
            >
              <Bounce duration={1000}>
                <img src={Checkmark} alt="checked" />
              </Bounce>
            </div>
          </button>
        </Fade>
      </div>
    </div>
  );
}
