import React, { Component , useState } from 'react';
import reactDom from 'react-dom';
//import {Button} from 'bootstrap/dist/css/bootstrap.css';
import './background.css';
import cont from './assets/bg-content.png';
import CustomModal from './customModal';

const modals = [
    {img : cont, text : "hello"},
];
 
export default function CustomButton(props){
    const{id,img,active,setActive,content} = props;
    const[alreadyClicked, setAlreadyClicked] = useState(false);

    const[isOpen, setIsOpen] = useState(false);



    function handleClick(){
        //setAlreadyClicked(true);
        setActive(id); 


    }

    function cancelClick(){
        setAlreadyClicked(true);
        setActive(id);

    }



  

    return(
        <button onMouseEnter ={handleClick} onMouseLeave={cancelClick} id = {id} 
        className={ active ? "active" : alreadyClicked ? "alreadyClicked" : "notClicked"}
         >

           <img src = {img} alt={id} />

           <div className = "modal-div">
                {modals.map((modal) => (
                    <CustomModal
                        img={modal.img}
                        text={modal.text}
                        />



                ))}

           </div>

        </button>
    )
}
