import React, { Component, useState } from "react";
import reactDom from "react-dom";
import BGimg from "./assets/bg.jpg";
import IconDotLine from "./assets/icon-dot-line.png";
import "./background.css";
import backgroundText from "./assets/text-intro.svg";
import CustomButton from "./customButton";
import IconOne from "./assets/icon-01.svg";
import IconTwo from "./assets/icon-02.svg";
import IconThree from "./assets/icon-03.svg";
import IconFour from "./assets/icon-04.svg";
import IconFive from "./assets/icon-05.svg";
import IconSix from "./assets/icon-06.svg";
import IconSeven from "./assets/icon-07.svg";
import IconEight from "./assets/icon-08.svg";
import IconNine from "./assets/icon-09.svg";
import Button from "react-bootstrap/Button";
import startButton from "./assets/button-start.svg";
import startHere from "./assets/start-here.png";
import CustomModal from "./customModal";
import content01 from "./assets/image-content-01.jpg";
import content02 from "./assets/image-content-02.jpg";
import content03 from "./assets/image-content-03.jpg";
import content04 from "./assets/image-content-04.jpg";
import content05 from "./assets/image-content-05.jpg";
import content06 from "./assets/image-content-06.jpg";
import content07 from "./assets/image-content-07.jpg";
import content08 from "./assets/image-content-08.jpg";
import content09 from "./assets/image-content-09.jpg";

const modals = [
  { id: "001", img: content01 },
  { id: "002", img: content02 },
  { id: "003", img: content03 },
  { id: "004", img: content04 },
  { id: "005", img: content05 },
  { id: "006", img: content06 },
  { id: "007", img: content07 },
  { id: "008", img: content08 },
  { id: "009", img: content09 },
];

const buttons = [
  { id: "num1", img: IconOne, mod : 1 },
  { id: "num2", img: IconTwo, mod : 2 },
  { id: "num3", img: IconThree, mod : 3 },
  { id: "num4", img: IconFour, mod : 4 },
  { id: "num5", img: IconFive, mod : 5 },
  { id: "num6", img: IconSix, mod : 6 },
  { id: "num7", img: IconSeven, mod : 7 },
  { id: "num8", img: IconEight, mod : 8 },
  { id: "num9", img: IconNine, mod : 9 },
];



export default function Background() {
  const [active, setActive] = useState(null);
  const [startpage, setStartPage] = useState(false);
  const [modalShow, setModalShow] = useState(null);

  function start(){
    setStartPage(true);
  }


  return (
    <div className="Background"
    >
      <div>
      <img src ={backgroundText} id="backText" />
        <Button className = "start-btn" onClick={start} style={{ display: startpage ? "none" : "block" }}>
       <img src = {startButton} />
     </Button>
      </div>
   

      <div style={{ display: startpage ? "block" : "none" }}>
        <img src={IconDotLine} id="IconSetup" />
        <img src = {startHere} id="startHereImage" />
      ;
        <h1> </h1>

        <div className="button-div">
          {buttons.map((button) => (
            <CustomButton
              id={button.id}
              img={button.img}
              active={active === button.id ? true : false}
              
              setActive={(id) => setActive(id)}
              
            />
          ))}
        </div>

          
      <div className = "modal-div"  >

<CustomModal
 id={active ? active.replace('num','') : null}
 img={modals.img}
 show={modalShow}
 onHide={() => setModalShow(false)}

 
/>

</div>

       
      </div>
    </div>
  );
}
