import React, { Component, useState } from "react";
import reactDom from "react-dom";
//import {Button} from 'bootstrap/dist/css/bootstrap.css';
import "./background.css";
import {Modal, Button } from 'bootstrap/dist/css/bootstrap.css';
import cont from './assets/bg-content.png';
import CustomModal from "./customModal";

export default function CustomButton(props) {
  const { id, img, active, setActive } = props;
  const [alreadyClicked, setAlreadyClicked] = useState(false);

  const [modalShow, setModalShow] = useState(false);


  function handleClick() {
    //setAlreadyClicked(true);
    setActive(id);
    setModalShow(true);
  }

  function cancelClick() {
    setAlreadyClicked(true);
    setActive(id);
  }

  return (
    <div>
      <button
        onClick={handleClick}
        onMouseLeave={cancelClick}
        id={id}
       
      
      >
        <img src= {img} id={
          active ? "active" : alreadyClicked ? "alreadyClicked" : "notClicked"
        }/>
      
      </button>

      <CustomModal 
      show = {modalShow}
      onHide = {() => setModalShow(false)}
      />




    </div>
  );
}
